import React, { useState, useEffect } from "react";
import axios from "axios";

const AdminPanel = ({ token }) => {
  const [players, setPlayers] = useState([]);
  const [editPlayer, setEditPlayer] = useState(null);
  const [formData, setFormData] = useState({
    number: "",
    name: "",
    position: "",
    birthdate: "",
    age: "",
    nation: "",
    contract: "",
    value: "",
  });

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/players", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((response) => setPlayers(response.data))
      .catch((error) => console.error("Fehler beim Abrufen der Spieler:", error));
  }, [token]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editPlayer) {
        await axios.put(`http://localhost:5000/api/players/${editPlayer.id}`, formData, {
          headers: { Authorization: `Bearer ${token}` },
        });
        alert("Spieler aktualisiert!");
      } else {
        await axios.post("http://localhost:5000/api/players", formData, {
          headers: { Authorization: `Bearer ${token}` },
        });
        alert("Spieler hinzugefügt!");
      }
      setFormData({
        number: "",
        name: "",
        position: "",
        birthdate: "",
        age: "",
        nation: "",
        contract: "",
        value: "",
      });
      setEditPlayer(null);
      axios
        .get("http://localhost:5000/api/players", {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => setPlayers(response.data));
    } catch (error) {
      alert("Fehler beim Speichern des Spielers");
    }
  };

  const handleEdit = (player) => {
    setEditPlayer(player);
    setFormData(player);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/players/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("Spieler gelöscht!");
      setPlayers(players.filter((player) => player.id !== id));
    } catch (error) {
      alert("Fehler beim Löschen des Spielers");
    }
  };

  return (
    <div>
      <h2 className="text-2xl font-bold mb-4">
        {editPlayer ? "Spieler bearbeiten" : "Spieler hinzufügen"}
      </h2>
      <form onSubmit={handleSubmit} className="space-y-4 max-w-md">
        <div>
          <label className="block text-sm font-medium">Trikotnummer</label>
          <input
            type="number"
            name="number"
            placeholder="Trikotnummer"
            value={formData.number}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div>
          <label className="block text-sm font-medium">Name</label>
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={formData.name}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div>
          <label className="block text-sm font-medium">Position</label>
          <select
            name="position"
            value={formData.position}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            required
          >
            <option value="">Position wählen</option>
            <option value="Torwart">Torwart</option>
            <option value="Abwehr">Abwehr</option>
            <option value="Mittelfeld">Mittelfeld</option>
            <option value="Sturm">Sturm</option>
          </select>
        </div>
        <div>
          <label className="block text-sm font-medium">Geburtsdatum</label>
          <input
            type="text"
            name="birthdate"
            placeholder="z.B. 12.05.1993"
            value={formData.birthdate}
            onChange={handleChange}
            className="w-full p-2 border rounded"
          />
        </div>
        <div>
          <label className="block text-sm font-medium">Alter</label>
          <input
            type="number"
            name="age"
            placeholder="Alter"
            value={formData.age}
            onChange={handleChange}
            className="w-full p-2 border rounded"
          />
        </div>
        <div>
          <label className="block text-sm font-medium">Nation</label>
          <input
            type="text"
            name="nation"
            placeholder="z.B. de"
            value={formData.nation}
            onChange={handleChange}
            className="w-full p-2 border rounded"
          />
        </div>
        <div>
          <label className="block text-sm font-medium">Vertrag bis</label>
          <input
            type="text"
            name="contract"
            placeholder="z.B. 2026"
            value={formData.contract}
            onChange={handleChange}
            className="w-full p-2 border rounded"
          />
        </div>
        <div>
          <label className="block text-sm font-medium">Marktwert</label>
          <input
            type="text"
            name="value"
            placeholder="z.B. 800 Tsd. €"
            value={formData.value}
            onChange={handleChange}
            className="w-full p-2 border rounded"
          />
        </div>
        <button
          type="submit"
          className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
        >
          {editPlayer ? "Aktualisieren" : "Hinzufügen"}
        </button>
      </form>
      <h2 className="text-2xl font-bold mt-8">Spielerliste</h2>
      <div className="overflow-x-auto">
        <table className="w-full bg-white dark:bg-gray-800 shadow-md rounded-lg">
          <thead className="bg-gray-200 dark:bg-gray-700">
            <tr>
              <th className="p-3 text-left">Nr.</th>
              <th className="p-3 text-left">Name</th>
              <th className="p-3 text-left">Position</th>
              <th className="p-3 text-left">Aktionen</th>
            </tr>
          </thead>
          <tbody>
            {players.map((player) => (
              <tr
                key={player.id}
                className="border-b dark:border-gray-700 hover:bg-gray-50 dark:hover:bg-gray-600"
              >
                <td className="p-3">{player.number}</td>
                <td className="p-3">{player.name}</td>
                <td className="p-3">{player.position}</td>
                <td className="p-3">
                  <button
                    onClick={() => handleEdit(player)}
                    className="mr-2 bg-yellow-500 text-white px-3 py-1 rounded hover:bg-yellow-600"
                  >
                    Bearbeiten
                  </button>
                  <button
                    onClick={() => handleDelete(player.id)}
                    className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                  >
                    Löschen
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default AdminPanel;
