import React, { useState, useEffect } from "react";
import axios from "axios";

const PlayerList = () => {
  const [players, setPlayers] = useState([]);
  const [filter, setFilter] = useState("");
  const [sortBy, setSortBy] = useState("number");
  const [view, setView] = useState("cards");

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/players")
      .then((response) => {
        console.log("Geladene Spieler:", response.data);
        setPlayers(response.data);
      })
      .catch((error) => console.error("Fehler beim Abrufen der Spieler:", error));
  }, []);

  const sortedPlayers = [...players].sort((a, b) => {
    if (sortBy === "position") {
      const positionOrder = ["Torwart", "Abwehr", "Mittelfeld", "Sturm"];
      return positionOrder.indexOf(a.position) - positionOrder.indexOf(b.position);
    }
    return a[sortBy] - b[sortBy];
  });

  const filteredPlayers = sortedPlayers.filter((player) =>
    player.name.toLowerCase().includes(filter.toLowerCase())
  );

  // Berechne Kaderwert
  const calculateKaderValue = () => {
    return filteredPlayers
      .reduce((total, player) => {
        if (!player.value) return total;
        const value = player.value.replace(/[^\d,.]/g, "").replace(",", ".");
        return total + parseFloat(value) * (player.value.includes("Mio") ? 1 : 0.001);
      }, 0)
      .toFixed(2)
      .replace(".", ",") + " Mio. â‚¬";
  };

  return (
    <div className="space-y-6">
     
      <div className="flex flex-col sm:flex-row sm:space-x-4 items-center">
        <input
          type="text"
          placeholder="Spieler suchen..."
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          className="input flex-1"
        />
        <select
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
          className="select"
        >
          <option value="number">Nach Nummer sortieren</option>
          <option value="position">Nach Position sortieren</option>
        </select>
        <div className="flex space-x-2">
          <button
            onClick={() => setView("cards")}
            className={`button px-4 py-2 ${view === "cards" ? "bg-primary" : "bg-accent"}`}
          >
            Karten
          </button>
          <button
            onClick={() => setView("list")}
            className={`button px-4 py-2 ${view === "list" ? "bg-primary" : "bg-accent"}`}
          >
            Liste
          </button>
        </div>
      </div>
      {view === "cards" ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
          {filteredPlayers.map((player) => (
            <div key={player.id} className="card">
              <div className="flex justify-between items-start">
                <h3 className="text-base font-semibold">{player.name}</h3>
                <span className="text-xs font-medium text-gray-500">Nr. {player.number}</span>
              </div>
              <div className="mt-2 flex items-center space-x-2">
                <span className="badge">{player.position}</span>
                {player.specificPosition && (
                  <span className={`specific-badge specific-${player.specificPosition.toLowerCase()}`}>
                    {player.specificPosition}
                  </span>
                )}
              </div>
              <div className="mt-2 space-y-1 text-xs">
                <p>Geburtsdatum: {player.birthdate || "-"}</p>
                <p>Alter: {player.age || "-"}</p>
                <p>
                  Nation:{" "}
                  {player.nation ? (
                    <span className={`flag-icon fi fi-${player.nation.toLowerCase()}`}></span>
                  ) : (
                    "-"
                  )}
                </p>
                <p>Vertrag: {player.contract || "-"}</p>
                <p>Wert: {player.value || "-"}</p>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="space-y-4">
          {filteredPlayers.map((player) => (
            <div key={player.id} className="list-item">
              <div className="flex items-center space-x-4">
                <span className="text-sm font-medium text-gray-500 w-12">Nr. {player.number}</span>
                <div className="flex-1">
                  <h3 className="text-base font-semibold">
                    {player.specificPosition ? (
                      <span className={`specific-badge specific-${player.specificPosition.toLowerCase()} mx-1`}>
                        {player.specificPosition}
                      </span>
                    ) : (
                      <span className="badge mx-1">{player.position || "-"}</span>
                    )}
                    {player.nation ? (
                      <span className={`flag-icon fi fi-${player.nation.toLowerCase()} mx-1`}></span>
                    ) : (
                      <span className="mx-1">-</span>
                    )}
                    {player.name}
                  </h3>
                </div>
                <div className="text-sm">
                  <span className="value-badge">{player.value || "-"}</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
      <div className="kader-value">
        Gesamtkaderwert: {calculateKaderValue()}
      </div>
    </div>
  );
};

export default PlayerList;
