 import React, { useState, useEffect } from "react";
import axios from "axios";

const AdminPanel = () => {
  const [players, setPlayers] = useState([]);
  const [editPlayer, setEditPlayer] = useState(null);
  const [formData, setFormData] = useState({
    number: "",
    name: "",
    position: "",
    birthdate: "",
    age: "",
    nation: "",
    contract: "",
    value: "",
  });

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/players")
      .then((response) => setPlayers(response.data))
      .catch((error) => console.error("Fehler beim Abrufen der Spieler:", error));
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editPlayer) {
        await axios.put(`http://localhost:5000/api/players/${editPlayer._id}`, formData);
        alert("Spieler aktualisiert!");
      } else {
        await axios.post("http://localhost:5000/api/players", formData);
        alert("Spieler hinzugefügt!");
      }
      setFormData({
        number: "",
        name: "",
        position: "",
        birthdate: "",
        age: "",
        nation: "",
        contract: "",
        value: "",
      });
      setEditPlayer(null);
      axios
        .get("http://localhost:5000/api/players")
        .then((response) => setPlayers(response.data));
    } catch (error) {
      alert("Fehler beim Speichern des Spielers");
    }
  };

  const handleEdit = (player) => {
    setEditPlayer(player);
    setFormData(player);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/players/${id}`);
      alert("Spieler gelöscht!");
      setPlayers(players.filter((player) => player._id !== id));
    } catch (error) {
      alert("Fehler beim Löschen des Spielers");
    }
  };

  return (
    <div>
      <h2>{editPlayer ? "Spieler bearbeiten" : "Spieler hinzufügen"}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="number"
          name="number"
          placeholder="Trikotnummer"
          value={formData.number}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="position"
          placeholder="Position"
          value={formData.position}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="birthdate"
          placeholder="Geburtsdatum (z.B. 12.05.1993)"
          value={formData.birthdate}
          onChange={handleChange}
        />
        <input
          type="number"
          name="age"
          placeholder="Alter"
          value={formData.age}
          onChange={handleChange}
        />
        <input
          type="text"
          name="nation"
          placeholder="Nation (z.B. de)"
          value={formData.nation}
          onChange={handleChange}
        />
        <input
          type="text"
          name="contract"
          placeholder="Vertrag bis (z.B. 2026)"
          value={formData.contract}
          onChange={handleChange}
        />
        <input
          type="text"
          name="value"
          placeholder="Marktwert (z.B. 800 Tsd. €)"
          value={formData.value}
          onChange={handleChange}
        />
        <button type="submit">{editPlayer ? "Aktualisieren" : "Hinzufügen"}</button>
      </form>
      <h2 className="mt-8">Spielerliste</h2>
      <table>
        <thead>
          <tr>
            <th>Nr.</th>
            <th>Name</th>
            <th>Position</th>
            <th>Aktionen</th>
          </tr>
        </thead>
        <tbody>
          {players.map((player) => (
            <tr key={player._id}>
              <td>{player.number}</td>
              <td>{player.name}</td>
              <td>{player.position}</td>
              <td>
                <button
                  onClick={() => handleEdit(player)}
                  className="mr-2 bg-yellow-500 text-white px-2 py-1 rounded"
                >
                  Bearbeiten
                </button>
                <button
                  onClick={() => handleDelete(player._id)}
                  className="bg-red-500 text-white px-2 py-1 rounded"
                >
                  Löschen
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPanel;
