import React, { useState, useEffect } from "react";
import { Routes, Route, Link, Navigate } from "react-router-dom";
import PlayerList from "./components/PlayerList";
import AdminPanel from "./components/AdminPanel";
import JsonImport from "./components/JsonImport";
import Login from "./components/Login";
import "./App.css";

const App = () => {
  const [theme, setTheme] = useState(localStorage.getItem("theme") || "light");
  const [token, setToken] = useState(localStorage.getItem("token") || null);

  useEffect(() => {
    document.documentElement.setAttribute("data-theme", theme);
    localStorage.setItem("theme", theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme(theme === "light" ? "dark" : "light");
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setToken(null);
  };

  return (
    <div className="container mx-auto p-4">
      <header className="flex justify-between items-center mb-4">
        <h1 className="text-2xl font-bold">VfL Bochum Kader-Management</h1>
        <nav>
          <Link to="/" className="mr-4">Kader</Link>
          {token ? (
            <>
              <Link to="/admin" className="mr-4">Admin</Link>
              <Link to="/import" className="mr-4">JSON-Import</Link>
              <button onClick={handleLogout} className="mr-4 text-red-500">
                Logout
              </button>
            </>
          ) : (
            <Link to="/login" className="mr-4">Login</Link>
          )}
        </nav>
        <button
          onClick={toggleTheme}
          className="px-4 py-2 bg-blue-500 text-white rounded"
        >
          {theme === "light" ? "Darkmode" : "Lightmode"}
        </button>
      </header>
      <Routes>
        <Route path="/" element={<PlayerList />} />
        <Route
          path="/admin"
          element={token ? <AdminPanel token={token} /> : <Navigate to="/login" />}
        />
        <Route
          path="/import"
          element={token ? <JsonImport token={token} /> : <Navigate to="/login" />}
        />
        <Route path="/login" element={<Login setToken={setToken} />} />
      </Routes>
    </div>
  );
};

export default App;
