import React, { useState, useEffect } from "react";
import axios from "axios";

const PlayerList = () => {
  const [players, setPlayers] = useState([]);
  const [filter, setFilter] = useState("");
  const [sortBy, setSortBy] = useState("number");
  const [view, setView] = useState("cards");

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/players")
      .then((response) => {
        console.log("Geladene Spieler:", response.data);
        setPlayers(response.data);
      })
      .catch((error) => console.error("Fehler beim Abrufen der Spieler:", error));
  }, []);

  // Alter berechnen
  const calculateAge = (birthdate) => {
    if (!birthdate) return "-";
    const today = new Date();
    const birth = new Date(birthdate);
    let age = today.getFullYear() - birth.getFullYear();
    const monthDiff = today.getMonth() - birth.getMonth();
    if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birth.getDate())) {
      age--;
    }
    return age;
  };

  // Vertrag formatieren
  const formatContract = (contract) => {
    if (!contract) return "-";
    const date = new Date(contract);
    return date.toLocaleDateString("de-DE", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
    });
  };

  // Sortierung
  const sortedPlayers = [...players].sort((a, b) => {
    if (sortBy === "position") {
      const positionOrder = ["Torvald", "Abwehr", "Mittelfeld", "Sturm"];
      return positionOrder.indexOf(a.position) - positionOrder.indexOf(b.position);
    } else if (sortBy === "name") {
      return a.name.localeCompare(b.name);
    } else if (sortBy === "age") {
      const ageA = a.age || calculateAge(a.birthdate) || 0;
      const ageB = b.age || calculateAge(b.birthdate) || 0;
      return ageA - ageB;
    } else if (sortBy === "contract") {
      const dateA = a.contract ? new Date(a.contract).getTime() : 0;
      const dateB = b.contract ? new Date(b.contract).getTime() : 0;
      return dateA - dateB;
    } else if (sortBy === "value") {
      const valueA = a.value ? parseFloat(a.value.replace(/[^\d,.]/g, "").replace(",", ".")) : 0;
      const valueB = b.value ? parseFloat(b.value.replace(/[^\d,]/g, "").replace(",", ".")) : 0;
      return valueA - valueB;
    }
    return a[sortBy] - b[sortBy];
  });

  const filteredPlayers = sortedPlayers.filter((player) =>
    player.name.toLowerCase().includes(filter.toLowerCase())
  );

  // Berechne Kaderwert
  const calculateKaderValue = () => {
    return filteredPlayers
      .reduce((total, player) => {
        if (!player.value) return total;
        const value = player.value.replace(/[^\d,.]/g, "").replace(",", ".");
        return total + parseFloat(value) * (player.value.includes("Mio") ? 1 : 0.001);
      }, 0)
      .toFixed(0)
      .replace(".", ",") + " Mio. â‚¬";
  };

  return (
    <div className="space-y-6">
      {/* Test-Flagge */}
      <p>Test-Flagge: <span className="flag-icon"></span></p>
      <div className="flex flex-col sm:flex-row sm:space-x-4 items-center">
        <input
          type="text"
          placeholder="Spieler suchen..."
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          className="input flex-1"
        />
        <select
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
          className="sort"
        >
          <option value="number">Nach Nummer</option>
          <option value="name">Nach Name</option>
          <option value="position">Nach Position</option>
          <option value="age">Nach Alter</option>
          <option value="contract">Nach Vertrag</option>
          <option value="value">Nach Marktwert</option>
        </select>
        <div className="flex space-x-2">
          <button
            onClick={() => setView("cards")}
            className={`button px-4 py-2 ${view === "cards" ? "bg-primary-dark" : "bg-accent"}`}
          >
            Cards
          </button>
          <button
            onClick={() => setView("list")}
            className={`button px-4 py-2 ${view === "list" ? "bg-primary-dark" : "bg-accent"}`}
          >
            Liste
          </button>
        </div>
      </div>
      {view === "cards" ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-2">
          {filteredPlayers.map((player) => (
            <div key={player.id} className="card">
              <div className="flex justify-between items-start">
                <h3 className="text-base font-semibold">{player.name}</h3>
                <span className="text-xs font-medium text-gray-500">Nr. {player.number}</span>
              </div>
              <div className="mt-2 grid grid-cols-2 gap-2 text-xs">
                <div>
                  <p><strong>Position:</strong> {player.position}</p>
                  {player.specificPosition && (
                    <p><strong>Spezifisch:</strong> <span className={`specific-badge specific-${player.specificPosition.toLowerCase()}`}>
                      {player.specificPosition}
                    </span></p>
                  )}
                </div>
                <div>
                  <p><strong>Nation:</strong>{" "}
                    {player.nation ? (
                      <span className={`flag-icon fi fi-${player.nation.toLowerCase()}`}></span>
                    ) : "-"}
                  </p>
                  <p><strong>Alter:</strong> {player.age || calculateAge(player.birthdate) || "-"}</p>
                  <p><strong>Vertrag:</strong> {formatContract(player.contract)}</p>
                  <p><strong>Wert:</strong> {player.value || "-"}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="space-y-2">
          <div className="grid grid-cols-8 gap-2 bg-primary-dark text-secondary p-2 rounded-t">
            <div className="cursor-pointer" onClick={() => setSortBy("number")}>
              Nr. {sortBy === "number" && (sortBy === "number" ? "â†‘" : "â†“")}
            </div>
            <div>Position</div>
            <div>Nation</div>
            <div className="cursor-pointer" onClick={() => setSortBy("name")}>
              Name {sortBy === "name" && (sortBy === "name" ? "â†‘" : "â†“")}
            </div>
            <div>Spez. Pos.</div>
            <div className="cursor-pointer" onClick={() => setSortBy("age")}>
              Alter {sortBy === "age" && (sortBy === "age" ? "â†‘" : "â†“")}
            </div>
            <div className="cursor-pointer" onClick={() => setSortBy("contract")}>
              Vertrag {sortBy === "contract" && (sortBy === "contract" ? "â†‘" : "â†“")}
            </div>
            <div className="cursor-pointer" onClick={() => setSortBy("value")}>
              Marktwert {sortBy === "value" && (sortBy === "value" ? "â†‘" : "â†“")}
            </div>
          </div>
          {filteredPlayers.map((player) => (
            <div
              key={player.id}
              className="list-item grid grid-cols-8 gap-2 bg-card-bg p-2 rounded shadow hover:bg-accent"
            >
              <div>{player.number}</div>
              <div>
                {player.specificPosition ? (
                  <span className={`specific-badge specific-${player.specificPosition.toLowerCase()}`}>
                    {player.specificPosition}
                  </span>
                ) : (
                  <span className="badge">{player.position || "-"}</span>
                )}
              </div>
              <div>
                {player.nation ? (
                  <span className={`flag-icon fi fi-${player.nation.toLowerCase()}`}></span>
                ) : (
                  "-"
                )}
              </div>
              <div>{player.name}</div>
              <div>{player.specificPosition || "-"}</div>
              <div>{player.age || calculateAge(player.birthdate) || "-"}</div>
              <div>{formatContract(player.contract)}</div>
              <div>
                <span className="value-badge">{player.value || "-"}</span>
              </div>
            </div>
          ))}
        </div>
      )}
      <div className="kader-value">
        Gesamtkaderwert: {calculateKaderValue()}
      </div>
    </div>
  );
};

export default PlayerList;
