import React, { useState, useEffect } from "react";
import axios from "axios";

const PlayerList = () => {
  const [players, setPlayers] = useState([]);
  const [filter, setFilter] = useState("");
  const [sortBy, setSortBy] = useState("number");
  const [view, setView] = useState("cards");

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/players")
      .then((response) => setPlayers(response.data))
      .catch((error) => console.error("Fehler beim Abrufen der Spieler:", error));
  }, []);

  const positionColors = {
    Torwart: "bg-green-500",
    Abwehr: "bg-blue-500",
    Mittelfeld: "bg-yellow-500",
    Sturm: "bg-red-500",
  };

  const sortedPlayers = [...players].sort((a, b) => {
    if (sortBy === "position") {
      const positionOrder = ["Torwart", "Abwehr", "Mittelfeld", "Sturm"];
      return positionOrder.indexOf(a.position) - positionOrder.indexOf(b.position);
    }
    return a[sortBy] - b[sortBy];
  });

  const filteredPlayers = sortedPlayers.filter((player) =>
    player.name.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row sm:space-x-4">
        <input
          type="text"
          placeholder="Spieler suchen..."
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          className="input flex-1"
        />
        <select
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
          className="select"
        >
          <option value="number">Nach Nummer sortieren</option>
          <option value="position">Nach Position sortieren</option>
        </select>
        <select
          value={view}
          onChange={(e) => setView(e.target.value)}
          className="select"
        >
          <option value="cards">Kartenansicht</option>
          <option value="table">Tabellenansicht</option>
        </select>
      </div>
      {view === "cards" ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredPlayers.map((player) => (
            <div key={player.id} className="card">
              <div className="flex justify-between items-start">
                <h3 className="text-lg font-semibold">{player.name}</h3>
                <span className="text-sm font-medium text-gray-500">Nr. {player.number}</span>
              </div>
              <span className={`badge ${positionColors[player.position]} mt-2`}>
                {player.position}
              </span>
              <div className="mt-2 space-y-1 text-sm">
                <p>Geburtsdatum: {player.birthdate || "-"}</p>
                <p>Alter: {player.age || "-"}</p>
                <p>Nation: {player.nation || "-"}</p>
                <p>Vertrag: {player.contract || "-"}</p>
                <p>Wert: {player.value || "-"}</p>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="overflow-x-auto">
          <table className="table">
            <thead>
              <tr>
                <th>Nr.</th>
                <th>Name</th>
                <th>Position</th>
                <th>Geburtsdatum</th>
                <th>Alter</th>
                <th>Nation</th>
                <th>Vertrag</th>
                <th>Wert</th>
              </tr>
            </thead>
            <tbody>
              {filteredPlayers.map((player) => (
                <tr key={player.id}>
                  <td>{player.number}</td>
                  <td>{player.name}</td>
                  <td>
                    <span className={`badge ${positionColors[player.position]}`}>
                      {player.position}
                    </span>
                  </td>
                  <td>{player.birthdate || "-"}</td>
                  <td>{player.age || "-"}</td>
                  <td>{player.nation || "-"}</td>
                  <td>{player.contract || "-"}</td>
                  <td>{player.value || "-"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default PlayerList;
